package tests

import (
	"bytes"
	"encoding/base64"
	"encoding/hex"
	"testing"

	"github.com/ethereum/go-ethereum/common"
	"github.com/stretchr/testify/require"

	fxtypes "github.com/functionx/fx-core/v7/types"
)

func TestFIP20Code(t *testing.T) {
	codeAddr := "0x79172102144e0ef3dc372c3774f3225fbba20066"
	codeBase64 := ""

	code, codeNew := replayCodeAddress(codeBase64, codeAddr, fxtypes.FIP20LogicAddress)
	require.Equal(t, "", hex.EncodeToString(code))
	require.Equal(t, "", hex.EncodeToString(codeNew))
	require.NotEqual(t, hex.EncodeToString(code), hex.EncodeToString(codeNew))
}

func TestWFXCode(t *testing.T) {
	codeAddr := "0x0f40EeCB63928367dcbf5Db326fD5A2824c76926"
	codeBase64 := ""

	code, codeNew := replayCodeAddress(codeBase64, codeAddr, fxtypes.WFXLogicAddress)
	require.Equal(t, "", hex.EncodeToString(code))
	require.Equal(t, "", hex.EncodeToString(codeNew))
	require.NotEqual(t, hex.EncodeToString(code), hex.EncodeToString(codeNew))
}

func replayCodeAddress(codeBase64, addr, addrNew string) (code, codeNew []byte) {
	bz, err := base64.StdEncoding.DecodeString(codeBase64)
	if err != nil {
		panic(err)
	}

	addr1 := common.HexToAddress(addr)
	addr2 := common.HexToAddress(addrNew)

	bzZero := bytes.ReplaceAll(bz, addr1.Bytes(), common.HexToAddress(fxtypes.EmptyEvmAddress).Bytes())
	bzNew := bytes.ReplaceAll(bz, addr1.Bytes(), addr2.Bytes())

	return bzZero, bzNew
}
