package keeper_test

import (
	"math/big"

	sdkmath "cosmossdk.io/math"
	sdk "github.com/cosmos/cosmos-sdk/types"
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/crypto"
	evmtypes "github.com/evmos/ethermint/x/evm/types"
	tmrand "github.com/tendermint/tendermint/libs/rand"

	"github.com/functionx/fx-core/v7/testutil/helpers"
	fxtypes "github.com/functionx/fx-core/v7/types"
)

func (suite *KeeperTestSuite) TestKeeper_CreateContractWithCode() {
	// CreateContractWithCode is in init genesis
	account := suite.app.EvmKeeper.GetAccount(suite.ctx, fxtypes.GetWFX().Address)
	suite.NotNil(account)

	suite.Equal(uint64(0), account.Nonce)
	code := suite.app.EvmKeeper.GetCode(suite.ctx, common.BytesToHash(account.CodeHash))
	suite.Equal(fxtypes.GetWFX().Code, code)
}

func (suite *KeeperTestSuite) TestKeeper_UpdateContractCode() {
	updateCode := []byte{1, 2, 3}
	err := suite.app.EvmKeeper.UpdateContractCode(suite.ctx, fxtypes.GetWFX().Address, updateCode)
	suite.NoError(err)

	account := suite.app.EvmKeeper.GetAccount(suite.ctx, fxtypes.GetWFX().Address)
	suite.NotNil(account)

	suite.Equal(uint64(0), account.Nonce)
	code := suite.app.EvmKeeper.GetCode(suite.ctx, common.BytesToHash(account.CodeHash))
	suite.Equal(updateCode, code)
}

func (suite *KeeperTestSuite) TestKeeper_DeployContract() {
	erc1967Proxy := fxtypes.GetERC1967Proxy()
	erc20 := fxtypes.GetFIP20()
	contract, err := suite.app.EvmKeeper.DeployContract(suite.ctx, suite.signer.Address(), erc1967Proxy.ABI, erc1967Proxy.Bin, erc20.Address, []byte{})
	suite.NoError(err)

	nonce, err := suite.app.AccountKeeper.GetSequence(suite.ctx, suite.signer.Address().Bytes())
	suite.NoError(err)

	contractAddr := crypto.CreateAddress(suite.signer.Address(), nonce-1)
	suite.Equal(contractAddr, contract)
}

func (suite *KeeperTestSuite) TestKeeper_DeployUpgradableContract() {
	erc20 := fxtypes.GetFIP20()
	initializeArgs := []interface{}{"FunctionX USD", "fxUSD", uint8(18), suite.app.Erc20Keeper.ModuleAddress()}
	contract, err := suite.app.EvmKeeper.DeployUpgradableContract(suite.ctx, suite.signer.Address(), erc20.Address, nil, &erc20.ABI, initializeArgs...)
	suite.NoError(err)

	nonce, err := suite.app.AccountKeeper.GetSequence(suite.ctx, suite.signer.Address().Bytes())
	suite.NoError(err)

	contractAddr := crypto.CreateAddress(suite.signer.Address(), nonce-1)
	suite.Equal(contractAddr, contract)
}

func (suite *KeeperTestSuite) TestKeeper_QueryContract() {
	erc20 := fxtypes.GetFIP20()
	initializeArgs := []interface{}{"FunctionX USD", "fxUSD", uint8(18), suite.app.Erc20Keeper.ModuleAddress()}
	contract, err := suite.app.EvmKeeper.DeployUpgradableContract(suite.ctx, suite.signer.Address(), erc20.Address, nil, &erc20.ABI, initializeArgs...)
	suite.NoError(err)

	var nameRes struct{ Value string }
	err = suite.app.EvmKeeper.QueryContract(suite.ctx, suite.signer.Address(), contract, erc20.ABI, "name", &nameRes)
	suite.NoError(err)
	suite.Equal(nameRes.Value, "FunctionX USD")

	var balanceRes struct{ Value *big.Int }
	err = suite.app.EvmKeeper.QueryContract(suite.ctx, suite.signer.Address(), contract, erc20.ABI, "balanceOf", &balanceRes, suite.signer.Address())
	suite.NoError(err)
	suite.Equal(big.NewInt(0).String(), balanceRes.Value.String())
}

func (suite *KeeperTestSuite) TestKeeper_ApplyContract() {
	erc20 := fxtypes.GetFIP20()
	initializeArgs := []interface{}{"FunctionX USD", "fxUSD", uint8(18), suite.app.Erc20Keeper.ModuleAddress()}
	contract, err := suite.app.EvmKeeper.DeployUpgradableContract(suite.ctx, suite.signer.Address(), erc20.Address, nil, &erc20.ABI, initializeArgs...)
	suite.NoError(err)

	mintAmt := int64(tmrand.Uint32())
	_, err = suite.app.EvmKeeper.ApplyContract(suite.ctx, suite.signer.Address(), contract, nil, erc20.ABI, "mint", suite.signer.Address(), big.NewInt(mintAmt))
	suite.NoError(err)

	var balanceRes struct{ Value *big.Int }
	err = suite.app.EvmKeeper.QueryContract(suite.ctx, suite.signer.Address(), contract, erc20.ABI, "balanceOf", &balanceRes, suite.signer.Address())
	suite.NoError(err)
	suite.Equal(big.NewInt(mintAmt).String(), balanceRes.Value.String())
}

func (suite *KeeperTestSuite) TestKeeper_UpgradableFIP20Check() {
	fip20 := fxtypes.GetFIP20()
	v3TestFIP20Code := fxtypes.MustDecodeHex("")
	v4TestFIP20Code := fxtypes.MustDecodeHex("")

	err := suite.app.EvmKeeper.CreateContractWithCode(suite.ctx, fip20.Address, v3TestFIP20Code)
	suite.NoError(err)

	initializeArgs := []interface{}{"FunctionX USD", "fxUSD", uint8(18), common.BytesToAddress(suite.app.AccountKeeper.GetModuleAddress(evmtypes.ModuleName))}
	fip20Address, err := suite.app.EvmKeeper.DeployUpgradableContract(suite.ctx, suite.signer.Address(), fip20.Address, nil, &fip20.ABI, initializeArgs...)
	suite.NoError(err)
	suite.CheckFIP20Method(fip20Address, "FunctionX USD", "fxUSD", uint8(18), big.NewInt(0), suite.signer.Address())
	// upgradeContract

	suite.Mint(fip20Address, suite.signer.Address(), big.NewInt(100))
	err = suite.app.EvmKeeper.UpdateContractCode(suite.ctx, fip20.Address, v4TestFIP20Code)
	suite.NoError(err)
	suite.Equal(common.Address{}.Bytes(), suite.Owner(fip20Address).Bytes())

	signer := helpers.NewSigner(helpers.NewEthPrivKey())
	helpers.AddTestAddr(suite.app, suite.ctx, signer.AccAddress(), sdk.NewCoins(sdk.NewCoin(fxtypes.DefaultDenom, sdkmath.NewInt(100).MulRaw(1e18))))
	suite.Approve(fip20Address, signer.Address(), big.NewInt(100))
	suite.Transfer(fip20Address, signer.Address(), big.NewInt(100))

	suite.True(suite.BalanceOf(fip20Address, signer.Address()).Cmp(big.NewInt(100)) == 0)

	err = suite.app.EvmKeeper.UpdateContractCode(suite.ctx, fip20.Address, fip20.Code)
	suite.NoError(err)

	suite.True(suite.BalanceOf(fip20Address, signer.Address()).Cmp(big.NewInt(100)) == 0)
	suite.CheckFIP20Method(fip20Address, "FunctionX USD", "fxUSD", uint8(18), big.NewInt(100), suite.signer.Address())
}

func (suite *KeeperTestSuite) TestKeeper_UpgradableWFXCheck() {
	wfx := fxtypes.GetWFX()
	v3TestWFXCode := fxtypes.MustDecodeHex("")
	v4TestWFXCode := fxtypes.MustDecodeHex("")

	err := suite.app.EvmKeeper.CreateContractWithCode(suite.ctx, wfx.Address, v3TestWFXCode)
	suite.NoError(err)

	initializeArgs := []interface{}{"Wrapped Function X", "WFX", uint8(18), common.BytesToAddress(suite.app.AccountKeeper.GetModuleAddress(evmtypes.ModuleName))}
	wfxAddress, err := suite.app.EvmKeeper.DeployUpgradableContract(suite.ctx, suite.signer.Address(), wfx.Address, nil, &wfx.ABI, initializeArgs...)
	suite.NoError(err)
	suite.CheckFIP20Method(wfxAddress, "Wrapped Function X", "WFX", uint8(18), big.NewInt(0), suite.signer.Address())
	// upgradeContract
	suite.Deposit(wfxAddress, big.NewInt(10000))
	suite.True(suite.BalanceOf(wfxAddress, suite.signer.Address()).Cmp(big.NewInt(10000)) == 0)

	err = suite.app.EvmKeeper.UpdateContractCode(suite.ctx, wfxAddress, v4TestWFXCode)
	suite.NoError(err)
	suite.Equal(common.Address{}.Bytes(), suite.Owner(wfxAddress).Bytes())

	signer := helpers.NewSigner(helpers.NewEthPrivKey())
	helpers.AddTestAddr(suite.app, suite.ctx, signer.AccAddress(), sdk.NewCoins(sdk.NewCoin(fxtypes.DefaultDenom, sdkmath.NewInt(100).MulRaw(1e18))))

	suite.Approve(wfxAddress, signer.Address(), big.NewInt(10000))
	suite.Transfer(wfxAddress, signer.Address(), big.NewInt(10000))

	suite.True(suite.BalanceOf(wfxAddress, signer.Address()).Cmp(big.NewInt(10000)) == 0)

	err = suite.app.EvmKeeper.UpdateContractCode(suite.ctx, wfxAddress, wfx.Code)
	suite.NoError(err)

	suite.True(suite.BalanceOf(wfxAddress, signer.Address()).Cmp(big.NewInt(10000)) == 0)

	suite.CheckFIP20Method(wfxAddress, "Wrapped Function X", "WFX", uint8(18), big.NewInt(10000), suite.signer.Address())
	suite.Deposit(wfxAddress, big.NewInt(10000))
	suite.True(suite.BalanceOf(wfxAddress, suite.signer.Address()).Cmp(big.NewInt(10000)) == 0)
	suite.Withdraw(wfxAddress, big.NewInt(10000))
	suite.True(suite.BalanceOf(wfxAddress, suite.signer.Address()).Cmp(big.NewInt(0)) == 0)
}
