package keeper_test

import (
	"bytes"
	"encoding/hex"
	"fmt"
	"math/big"

	"github.com/ethereum/go-ethereum/common"
	evmtypes "github.com/evmos/ethermint/x/evm/types"

	"github.com/pundiai/fx-core/v8/contract"
	"github.com/pundiai/fx-core/v8/testutil/helpers"
)

func (s *KeeperTestSuite) TestKeeper_ContractCode() {
	// 1. create contract with code
	erc20 := contract.GetERC20()
	err := s.App.EvmKeeper.CreateContractWithCode(s.Ctx, erc20.Address, erc20.Code)
	s.Require().NoError(err)

	s.AssertCode(erc20.Address, erc20.Code)

	// 2. update contract code
	updateCode := []byte{1, 2, 3}
	err = s.App.EvmKeeper.UpdateContractCode(s.Ctx, erc20.Address, updateCode)
	s.Require().NoError(err)

	s.AssertCode(erc20.Address, updateCode)
}

func (s *KeeperTestSuite) AssertCode(address common.Address, code []byte) {
	account := s.App.EvmKeeper.GetAccount(s.Ctx, address)
	s.Require().Equal(uint64(0), account.Nonce)
	codeHash := common.BytesToHash(account.CodeHash)

	s.Require().Equal(code, s.App.EvmKeeper.GetCode(s.Ctx, codeHash))
}

func (s *KeeperTestSuite) TestKeeper_DeployContract() {
	erc1967Proxy := contract.GetERC1967Proxy()
	erc20 := contract.GetERC20()
	sender := s.AddTestSigner().Address()
	newContractAddr, err := s.App.EvmKeeper.DeployContract(s.Ctx, sender,
		erc1967Proxy.ABI, erc1967Proxy.Bin, erc20.Address, []byte{})
	s.Require().NoError(err)

	s.AssertContractAddr(sender, newContractAddr)
}

func (s *KeeperTestSuite) TestKeeper_DeployUpgradableContract() {
	erc20Suite := s.NewERC20TokenSuite()
	newContractAddr := erc20Suite.DeployERC20Token(s.Ctx, "USD")

	s.AssertContractAddr(erc20Suite.HexAddress(), newContractAddr)
}

func (s *KeeperTestSuite) TestKeeper_UpdateContractCode_ERC20() {
	erc20 := contract.GetERC20()
	v3TestErc20Code := contract.MustDecodeHex("")
	err := s.App.EvmKeeper.CreateContractWithCode(s.Ctx, erc20.Address, v3TestErc20Code)
	s.Require().NoError(err)

	erc20Suite := s.NewERC20TokenSuite()
	erc20Suite.DeployERC20Token(s.Ctx, "USD")

	erc20Suite.OnTest(s.Ctx, "USD Token", "USD", uint8(18), big.NewInt(0), erc20Suite.HexAddress())

	newSigner := helpers.NewSigner(helpers.NewEthPrivKey())
	erc20Suite.Mint(s.Ctx, erc20Suite.HexAddress(), big.NewInt(100))
	erc20Suite.Transfer(s.Ctx, newSigner.Address(), big.NewInt(100))

	err = s.App.EvmKeeper.UpdateContractCode(s.Ctx, erc20.Address, erc20.Code)
	s.Require().NoError(err)

	erc20Suite.OnTest(s.Ctx, "USD Token", "USD", uint8(18), big.NewInt(100), erc20Suite.HexAddress())
	erc20Suite.TransferOwnership(s.Ctx, newSigner.Address())
}

func (s *KeeperTestSuite) TestCodeCheck() {
	for _, c := range []contract.Contract{contract.GetERC20(), contract.GetWPUNDIAI()} {
		contractOwner := s.AddTestSigner()
		addr, err := s.App.EvmKeeper.DeployContract(s.Ctx, contractOwner.Address(), c.ABI, c.Bin)
		s.NoError(err)

		account := s.App.EvmKeeper.GetAccount(s.Ctx, addr)
		code := s.App.EvmKeeper.GetCode(s.Ctx, common.BytesToHash(account.CodeHash))
		s.Equal(c.Code, code, fmt.Sprintf("new code: %s", hex.EncodeToString(code)))

		deployedCode := bytes.ReplaceAll(code, c.Address.Bytes(), common.Address{}.Bytes())
		s.True(bytes.HasSuffix(c.Bin, deployedCode))
	}
}

func (s *KeeperTestSuite) TestKeeper_UpdateContractCode_WarpToken() {
	warpToken := contract.GetWPUNDIAI()
	v3TestWarpTokenCode := contract.MustDecodeHex("")
	v4TestWarpTokenCode := contract.MustDecodeHex("")

	err := s.App.EvmKeeper.CreateContractWithCode(s.Ctx, warpToken.Address, v3TestWarpTokenCode)
	s.Require().NoError(err)

	erc20Suite := s.NewERC20TokenSuite()
	initializeArgs := []interface{}{"Wrapped Token", "WARP", uint8(18), common.BytesToAddress(s.App.AccountKeeper.GetModuleAddress(evmtypes.ModuleName))}
	warpTokenAddress, err := s.App.EvmKeeper.DeployUpgradableContract(s.Ctx, erc20Suite.HexAddress(), warpToken.Address, nil, &warpToken.ABI, initializeArgs...)
	s.Require().NoError(err)
	erc20Suite.WithContract(warpTokenAddress)

	erc20Suite.OnTest(s.Ctx, "Wrapped Token", "WARP", uint8(18), big.NewInt(0), erc20Suite.HexAddress())

	erc20Suite.Deposit(s.Ctx, big.NewInt(100))

	err = s.App.EvmKeeper.UpdateContractCode(s.Ctx, warpToken.Address, v4TestWarpTokenCode)
	s.Require().NoError(err)

	s.Require().True(erc20Suite.BalanceOf(s.Ctx, erc20Suite.HexAddress()).Cmp(big.NewInt(100)) == 0)

	newSigner := helpers.NewSigner(helpers.NewEthPrivKey())
	s.MintToken(newSigner.AccAddress(), helpers.NewStakingCoin(100, 18))
	erc20Suite.Transfer(s.Ctx, newSigner.Address(), big.NewInt(100))

	erc20Suite.Approve(s.Ctx, newSigner.Address(), big.NewInt(100))

	erc20Suite.Deposit(s.Ctx, big.NewInt(100))
	erc20Suite.Transfer(s.Ctx, newSigner.Address(), big.NewInt(100))
	erc20Suite.OnTest(s.Ctx, "Wrapped Token", "WARP", uint8(18), big.NewInt(200), erc20Suite.HexAddress())
	s.Require().True(erc20Suite.BalanceOf(s.Ctx, newSigner.Address()).Cmp(big.NewInt(200)) == 0)

	err = s.App.EvmKeeper.UpdateContractCode(s.Ctx, warpToken.Address, warpToken.Code)
	s.Require().NoError(err)

	erc20Suite.Burn(s.Ctx, newSigner.Address(), big.NewInt(100))
	erc20Suite.Deposit(s.Ctx, big.NewInt(100))
	erc20Suite.WithdrawSelf(s.Ctx, big.NewInt(50))
	erc20Suite.Withdraw(s.Ctx, newSigner.Address(), big.NewInt(50))

	erc20Suite.OnTest(s.Ctx, "Wrapped Token", "WARP", uint8(18), big.NewInt(100), erc20Suite.HexAddress())
}
